sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // Parse the JSON response
        var responseObj = JSON.parse(inputs.response);
        
        // Extract the image URL
        var imageUrl = '';
        if (responseObj && responseObj.data && responseObj.data.length > 0 && responseObj.data[0].url) {
            imageUrl = responseObj.data[0].url;
        } else {
            gs.error("Failed to extract URL from response");
            outputs.success = false;
            return;
        }
        
        // Log for debugging
        gs.info("Attempting to download from URL: " + imageUrl);
        
        // Get the target record ID
        var ritmSysID = inputs.ritmSysId;
        
        // Create REST message
        var restMessage = new sn_ws.RESTMessageV2();
        restMessage.setHttpMethod('GET');
        restMessage.setEndpoint(imageUrl);
        
        // Important: Set these headers to ensure proper handling of binary data
        restMessage.setRequestHeader("Accept", "image/png");
        
        // This is the key method - directly save the response as an attachment to the RITM
        var fileName = 'image_' + new GlideDateTime().getNumericValue() + '.png';
        
        // Use saveResponseBodyAsAttachment to directly save to the RITM
        restMessage.saveResponseBodyAsAttachment('sc_req_item', ritmSysID, fileName);
        
        // Execute the request
        var response = restMessage.execute();
        var statusCode = response.getStatusCode();
        
        gs.info("HTTP status code: " + statusCode);
        
        if (statusCode == 200) {
            // Look up the newly created attachment
            var attachGR = new GlideRecord('sys_attachment');
            attachGR.addQuery('table_name', 'sc_req_item');
            attachGR.addQuery('table_sys_id', ritmSysID);
            attachGR.addQuery('file_name', fileName);
            attachGR.orderByDesc('sys_created_on');
            attachGR.setLimit(1);
            attachGR.query();
            
            if (attachGR.next()) {
                gs.info("Successfully created attachment: " + attachGR.getUniqueValue());
                outputs.success = true;
                outputs.attachmentId = attachGR.getUniqueValue();
            } else {
                gs.error("Attachment not found after creation");
                outputs.success = false;
            }
        } else {
            gs.error("HTTP request failed with status: " + statusCode);
            outputs.success = false;
        }
    } catch (ex) {
        gs.error("Error in script execution: " + ex.message);
        outputs.success = false;
        outputs.error = ex.message;
    }
})(inputs, outputs);

